/* eslint-disable react-hooks/rules-of-hooks */
import React, {useState} from 'react'
import {Link} from 'react-router-dom'
import logo from '../../assets/logos/invocom-log.png';
import invoNews from '../../assets/logos/invoNews.jpeg';
import Header from '../../Components/Header';
import Brand from '../../Components/Brand';
import '../../assets/styles/global.scss';
import Banner from '../../assets/logos/newsbanner.jpg'
const Blog = () => {
    const[show , setShow] = useState(true);

    const changeStates = () => {
      setShow(!show)
    }
    return (
      <div className="complete_bot absolute">

          { show? 
        (          <div className="widget">
            <Header />

        <div className="message_body w-lg h-lg border border-primary border-b-0 relative overflow-y-auto flex justify-center">
        <div className="w-md  bg-white shadow-lg rounded-lg my-12 border border-primary hover:bg-lightGrey">
  <div className="flex justify-center md:justify-end -mt-12">
    <img className="w-16 h-16 object-cover rounded-full" src={invoNews} alt=""/>
  </div>
  <div>
    <div className="py-0 -mt-1">
        <img src={Banner} alt=""/>
    </div>
    <div className="py-4 px-8">
    <h2 className="text-gray-800 text-3xl font-semibold">InvoNews</h2>
    <p className="mt-2 text-gray-600">Lorem ipsum dolor sit amet consectetur adipisicing elit doloremque natus error, rerum quas odio quaerat nam ex commodi hic, suscipit in a veritatis pariatur minus consequuntur!</p>

    </div>
   </div>
  <div className="flex justify-end mt-4">
    <a href="https://www.linkedin.com/company/invonews/" class="text-sm font-medium text-primary hover:text-indigo-500">Visit Page</a>
  </div>
</div>
        </div>

       <Brand />
        </div>)
        :
        (
        ''

        )  
        }

          <img onClick={changeStates} src={logo} alt="" />
      </div>
    )
}

export default Blog
